package com.supermap.pisaclient.entity;import java.util.ArrayList;import java.util.List;public class Category {	private CategoryItem mCategory;	private List<Product> mCategoryItem = new ArrayList<Product>();	public Category(CategoryItem mCategory) {		this.mCategory = mCategory;	}	public String getmCategoryName() {		return mCategory.CategoryName;	}	public void addItem(Product product) {		mCategoryItem.add(product);	}	public String getItem(int pPosition) {		if (pPosition == 0) {			return mCategory.CategoryName;		} else {			return mCategoryItem.get(pPosition - 1).ProductTitle;		}	}	public Product getObject(int pPosition) {		if (pPosition == 0) {			return mCategoryItem.get(0);		} else {			return mCategoryItem.get(pPosition - 1);		}	}	public int getItemCount() {		return mCategoryItem.size() + 1;	}}